Node.DataCrLf:
///Эта %Zen страница предназначена для редактирования класса :ASUJNLl.clCommand на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
///Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
///коментарии у свойств класса ASUJNLl.clCommand используются следующим образом:
/// ////Текст коментария;x;f;date;w
///    1   Текст коментария - заголовок колонки и коментарий в поле редактирования
///    2   x - код сортировки поля при формирования таблицы просмотра
///    3   f - нужен ли фильтр для этого поля
///    4   date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
///    5   w - значение параметра width при формировании панели навигации
 Class ASUADMm.CommandEdit Extends Template.TableEditor
{

///Параметр - заголовок таблицы
Parameter PAGENAME = "";

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUJNLl.clCommand" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="Action" cellTitle="Action"  header="?Action"/>
<column colName="BSLOGIN" cellTitle="BSLOGIN"  header="?BSLOGIN"/>
<column colName="CardID" cellTitle="CardID"  header="?CardID"/>
<column colName="Code" cellTitle="Code"  header="?Code"/>
<column colName="IP" cellTitle="IP"  header="?IP"/>
<column colName="Param" cellTitle="Param"  header="?Param"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<text id="Action" label="?Action" size="140" />
<text id="BSLOGIN" label="?BSLOGIN" size="140" />
<text id="CardID" label="?CardID" size="140" />
<text id="Code" label="?Code" size="140" />
<text id="IP" label="?IP" size="140" />
<text id="Param" label="?Param" size="140" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUJNLl.clCommand).%OpenId(Id)
	d Form.%page.%SetValueById("Action",cur.Action)
	d Form.%page.%SetValueById("BSLOGIN",cur.BSLOGIN)
	d Form.%page.%SetValueById("CardID",cur.CardID)
	d Form.%page.%SetValueById("Code",cur.Code)
	d Form.%page.%SetValueById("IP",cur.IP)
	d Form.%page.%SetValueById("Param",cur.Param)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUJNLl.clCommand).%New()
	else  s cur = ##class(ASUJNLl.clCommand).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.Action = Form.%page.%GetValueById("Action")
		s cur.BSLOGIN = Form.%page.%GetValueById("BSLOGIN")
		s cur.CardID = Form.%page.%GetValueById("CardID")
		s cur.Code = Form.%page.%GetValueById("Code")
		s cur.IP = Form.%page.%GetValueById("IP")
		s cur.Param = Form.%page.%GetValueById("Param")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUJNLl.clCommand).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("Action","")
	d ..%SetValueById("BSLOGIN","")
	d ..%SetValueById("CardID","")
	d ..%SetValueById("Code","")
	d ..%SetValueById("IP","")
	d ..%SetValueById("Param","")
	Quit $$$OK
}

 /// Добавить запись в базу данных.
 ///Пример > w ##class(ASUADMm.CommandEdit).Add("Action2","BSLOGIN2","CardID2","Code2","IP2","Param2")
ClassMethod Add(Action As %String = "" ,BSLOGIN As %String = "" ,CardID As %String = "" ,Code As %String = "" ,IP As %String = "" ,Param As %String = "" ) As %String
{
 S o=##class(ASUJNLl.clCommand).%New()
 s o.Action=Action
 s o.BSLOGIN=BSLOGIN
 s o.CardID=CardID
 s o.Code=Code
 s o.IP=IP
 s o.Param=Param
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
 /// Добавить запись в базу данных и возвратить новый ID в переменной newId.
 ///Пример > w ##class(ASUADMm.CommandEdit).AddRecId(.newId,"Action2","BSLOGIN2","CardID2","Code2","IP2","Param2")
ClassMethod AddRecId(newID As %String,Action As %String = "" ,BSLOGIN As %String = "" ,CardID As %String = "" ,Code As %String = "" ,IP As %String = "" ,Param As %String = "" ) As %String
{
 S o=##class(ASUJNLl.clCommand).%New()
 s o.Action=Action
 s o.BSLOGIN=BSLOGIN
 s o.CardID=CardID
 s o.Code=Code
 s o.IP=IP
 s o.Param=Param
 S sc=o.%Save()
 s newID=o.%Id()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

