 /// Добавить запись в базу данных и возвратить новый ID в переменной newId.
 ///Пример > w ##class(ASUADMm.MdbUpEdit).AddRecId(.newId,"aPC2","NameMdb2","aIP2","DateUpdate2","idMdb2","DescMdb2")
ClassMethod AddRecId(newID As %String,aPC As %String = "" ,NameMdb As %String = "" ,aIP As %String = "" ,DateUpdate As %String = "" ,idMdb As %String = "" ,DescMdb As %String = "" ) As %String
{
 S o=##class(ASUADMm.MdbUpdateLog).%New()
 s o.aPC=aPC
 s o.NameMdb=NameMdb
 s o.aIP=aIP
 s o.DateUpdate=DateUpdate
 s o.idMdb=idMdb
 s o.DescMdb=DescMdb
 S sc=o.%Save()
 s newID=o.%Id()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

///Эта %Zen страница предназначена для редактирования класса :ASUADMm.opt на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
///Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
///коментарии у свойств класса ASUADMm.opt используются следующим образом:
/// ////Текст коментария;x;f;date;w
///    1   Текст коментария - заголовок колонки и коментарий в поле редактирования
///    2   x - код сортировки поля при формирования таблицы просмотра
///    3   f - нужен ли фильтр для этого поля
///    4   date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
///    5   w - значение параметра width при формировании панели навигации
 Class ASUADMm.optEdit Extends Template.TableEditor
{

///Параметр - заголовок таблицы
Parameter PAGENAME = "параметры АРМ Администратор";

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUADMm.opt" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="NameOpt" cellTitle="NameOpt" width="160" filterType="text"  filterTitle="введите контекст фильтра"  header="имя параметра"/>
<column colName="DescOpt" cellTitle="DescOpt"  filterType="text"  filterTitle="введите контекст фильтра"  header="описание параметра"/>
<column colName="ValOpt" cellTitle="ValOpt"  filterType="text"  filterTitle="введите контекст фильтра"  header="значение параметра"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<text id="NameOpt" label="имя параметра" size="140" />
<text id="DescOpt" label="описание параметра" size="140" />
<text id="ValOpt" label="значение параметра" size="140" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUADMm.opt).%OpenId(Id)
	d Form.%page.%SetValueById("NameOpt",cur.NameOpt)
	d Form.%page.%SetValueById("DescOpt",cur.DescOpt)
	d Form.%page.%SetValueById("ValOpt",cur.ValOpt)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUADMm.opt).%New()
	else  s cur = ##class(ASUADMm.opt).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.NameOpt = Form.%page.%GetValueById("NameOpt")
		s cur.DescOpt = Form.%page.%GetValueById("DescOpt")
		s cur.ValOpt = Form.%page.%GetValueById("ValOpt")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUADMm.opt).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("NameOpt","")
	d ..%SetValueById("DescOpt","")
	d ..%SetValueById("ValOpt","")
	Quit $$$OK
}

 /// Добавить запись в базу данных.
 ///Пример > w ##class(ASUADMm.optEdit).Add("NameOpt2","DescOpt2","ValOpt2")
ClassMethod Add(NameOpt As %String = "" ,DescOpt As %String = "" ,ValOpt As %String = "" ) As %String
{
 S o=##class(ASUADMm.opt).%New()
 s o.NameOpt=NameOpt
 s o.DescOpt=DescOpt
 s o.ValOpt=ValOpt
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
 /// Добавить запись в базу данных и возвратить новый ID в переменной newId.
 ///Пример > w ##class(ASUADMm.optEdit).AddRecId(.newId,"NameOpt2","DescOpt2","ValOpt2")
ClassMethod AddRecId(newID As %String,NameOpt As %String = "" ,DescOpt As %String = "" ,ValOpt As %String = "" ) As %String
{
 S o=##class(ASUADMm.opt).%New()
 s o.NameOpt=NameOpt
 s o.DescOpt=DescOpt
 s o.ValOpt=ValOpt
 S sc=o.%Save()
 s newID=o.%Id()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

