/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUJNLl.clQRem).%OpenId(Id)
	d Form.%page.%SetValueById("Cmd",cur.Cmd)
	d Form.%page.%SetValueById("Prm",cur.Prm)
	d Form.%page.%SetValueById("ReadIt",cur.ReadIt)
	d Form.%page.%SetValueById("BSLogin",cur.BSLogin)
	d Form.%page.%SetValueById("idAction",cur.idAction)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUJNLl.clQRem).%New()
	else  s cur = ##class(ASUJNLl.clQRem).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.Cmd = Form.%page.%GetValueById("Cmd")
		s cur.Prm = Form.%page.%GetValueById("Prm")
		s cur.ReadIt = Form.%page.%GetValueById("ReadIt")
		s cur.BSLogin = Form.%page.%GetValueById("BSLogin")
		s cur.idAction = Form.%page.%GetValueById("idAction")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUJNLl.clQRem).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("Cmd","")
	d ..%SetValueById("Prm","")
	d ..%SetValueById("ReadIt","")
	d ..%SetValueById("BSLogin","")
	d ..%SetValueById("idAction","")
	Quit $$$OK
}

 /// Добавить запись в базу данных. 
 ///Пример > w ##class(ASUJNLl.clQRemEdit).Add("Cmd2","Prm2","ReadIt2","BSLogin2","idAction2")
ClassMethod Add(Cmd As %String = "" ,Prm As %String = "" ,ReadIt As %String = "" ,BSLogin As %String = "" ,idAction As %String = "" ) As %String
{
 S o=##class(ASUJNLl.clQRem).%New()
 s o.Cmd=Cmd
 s o.Prm=Prm
 s o.ReadIt=ReadIt
 s o.BSLogin=BSLogin
 s o.idAction=idAction
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

/// Эта %Zen страница предназначена для редактирования класса :ASUJNLl.clQRem на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
/// Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
/// коментарии у свойств класса ASUJNLl.clQRem используются следующим образом:
/// ////Текст коментария;x;f;date;w
///    1   Текст коментария - заголовок колонки и коментарий в поле редактирования
///    2   x - код сортировки поля при формирования таблицы просмотра
///    3   f - нужен ли фильтр для этого поля
///    4   date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
///    5   w - значение параметра width при формировании панели навигации
Class ASUJNLl.clQremEdit Extends Template.TableEditor
{

/// Параметр - заголовок таблицы
Parameter PAGENAME;

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

/// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUJNLl.clQRem" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="BSLogin" cellTitle="BSLogin"  header="?BSLogin"/>
<column colName="Cmd" cellTitle="Cmd"  header="?Cmd"/>
<column colName="Prm" cellTitle="Prm"  header="?Prm"/>
<column colName="ReadIt" cellTitle="ReadIt"  header="?ReadIt"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}

/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<text id="BSLogin" label="?BSLogin" size="140" />
<text id="Cmd" label="?Cmd" size="140" />
<text id="Prm" label="?Prm" size="140" />
<text id="ReadIt" label="?ReadIt" size="140" />
<!--</vgroup>-->
</pane>
}

