/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUNACm.Log" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="oDat" cellTitle="oDat"  filterType="date"  filterTitle="введите дату фильтра"  header="Дата формирования оповещения"/>
<column colName="oTime" cellTitle="oTime"  filterType="text"  filterTitle="введите контекст фильтра"  header="Время формирования оповещения"/>
<column colName="UserIP" cellTitle="UserIP"  filterType="text"  filterTitle="введите контекст фильтра"  header="Кто рассылал оповещение"/>
<column colName="oUserS" cellTitle="oUserS"  filterType="text"  filterTitle="введите контекст фильтра"  header="Кому посылались сообщения (перечень)"/>
<column colName="rou" cellTitle="rou"  filterType="text"  filterTitle="введите контекст фильтра"  header="Программа отрабатывающая оповещение"/>
<column colName="nsp" cellTitle="nsp"  filterType="text"  filterTitle="введите контекст фильтра"  header="Рабочая область, где лежит программа"/>
<column colName="Type" cellTitle="Type"  header="Тип оповещения (регулярность)???"/>
<column colName="Mess" cellTitle="Mess"  filterType="text"  filterTitle="введите контекст фильтра"  header="Текст сообщения"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<dateText id="oDat" label="Дата формирования оповещения" size="10" />
<text id="oTime" label="Время формирования оповещения" size="140" />
<text id="UserIP" label="Кто рассылал оповещение" size="140" />
<text id="oUserS" label="Кому посылались сообщения (перечень)" size="140" />
<text id="rou" label="Программа отрабатывающая оповещение" size="140" />
<text id="nsp" label="Рабочая область, где лежит программа" size="140" />
<text id="Type" label="Тип оповещения (регулярность)???" size="140" />
<text id="Mess" label="Текст сообщения" size="140" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUNACm.Log).%OpenId(Id)
	d Form.%page.%SetValueById("oDat",cur.oDat)
	d Form.%page.%SetValueById("oTime",cur.oTime)
	d Form.%page.%SetValueById("UserIP",cur.UserIP)
	d Form.%page.%SetValueById("oUserS",cur.oUserS)
	d Form.%page.%SetValueById("rou",cur.rou)
	d Form.%page.%SetValueById("nsp",cur.nsp)
	d Form.%page.%SetValueById("Type",cur.Type)
	d Form.%page.%SetValueById("Mess",cur.Mess)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUNACm.Log).%New()
	else  s cur = ##class(ASUNACm.Log).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.oDat = Form.%page.%GetValueById("oDat")
		s cur.oTime = Form.%page.%GetValueById("oTime")
		s cur.UserIP = Form.%page.%GetValueById("UserIP")
		s cur.oUserS = Form.%page.%GetValueById("oUserS")
		s cur.rou = Form.%page.%GetValueById("rou")
		s cur.nsp = Form.%page.%GetValueById("nsp")
		s cur.Type = Form.%page.%GetValueById("Type")
		s cur.Mess = Form.%page.%GetValueById("Mess")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUNACm.Log).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("oDat","")
	d ..%SetValueById("oTime","")
	d ..%SetValueById("UserIP","")
	d ..%SetValueById("oUserS","")
	d ..%SetValueById("rou","")
	d ..%SetValueById("nsp","")
	d ..%SetValueById("Type","")
	d ..%SetValueById("Mess","")
	Quit $$$OK
}

 /// Добавить запись в базу данных. 
 ///Пример > w ##class(ASUNACm.LogEdit).Add("oDat2","oTime2","UserIP2","oUserS2","rou2","nsp2","Type2","Mess2")
ClassMethod Add(oDat As %String = "" ,oTime As %String = "" ,UserIP As %String = "" ,oUserS As %String = "" ,rou As %String = "" ,nsp As %String = "" ,Type As %String = "" ,Mess As %String = "" ) As %String
{
 S o=##class(ASUNACm.Log).%New()
 s o.oDat=oDat
 s o.oTime=oTime
 s o.UserIP=UserIP
 s o.oUserS=oUserS
 s o.rou=rou
 s o.nsp=nsp
 s o.Type=Type
 s o.Mess=Mess
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
 /// Добавить запись в базу данных. 
 ///Пример > w ##class(ASUNACm.LogEdit).AddRecId(.newId,"oDat2","oTime2","UserIP2","oUserS2","rou2","nsp2","Type2","Mess2")
ClassMethod AddRecId(newID As %String,oDat As %String = "" ,oTime As %String = "" ,UserIP As %String = "" ,oUserS As %String = "" ,rou As %String = "" ,nsp As %String = "" ,Type As %String = "" ,Mess As %String = "" ) As %String
{
 S o=##class(ASUNACm.Log).%New()
 s o.oDat=oDat
 s o.oTime=oTime
 s o.UserIP=UserIP
 s o.oUserS=oUserS
 s o.rou=rou
 s o.nsp=nsp
 s o.Type=Type
 s o.Mess=Mess
 S sc=o.%Save()
 s newID=o.%Id()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

///Эта %Zen страница предназначена для редактирования класса :ASUNACm.opt на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
///Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
///коментарии у свойств класса ASUNACm.opt используются следующим образом:
///     ///Текст коментария;x;f;date
///       Текст коментария - заголовок колонки и коментарий в поле редактирования
///       x - код сортировки поля при формирования таблицы просмотра
///       f - нужен ли фильтр для этого поля
///       date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
 Class ASUNACm.optEdit Extends Template.TableEditor
{

///Параметр - заголовок таблицы
Parameter PAGENAME = "параметры АРМ Начальник";

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUNACm.opt" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="NameOpt"  filterType="text"  filterTitle="введите контекст фильтра"  header="имя параметра"/>
<column colName="DescOpt"  filterType="text"  filterTitle="введите контекст фильтра"  header="описание параметра"/>
<column colName="ValOpt"  filterType="text"  filterTitle="введите контекст фильтра"  header="значение параметра"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<text id="NameOpt" label="имя параметра" size="40" />
<text id="DescOpt" label="описание параметра" size="40" />
<text id="ValOpt" label="значение параметра" size="40" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUNACm.opt).%OpenId(Id)
	d Form.%page.%SetValueById("NameOpt",cur.NameOpt)
	d Form.%page.%SetValueById("DescOpt",cur.DescOpt)
	d Form.%page.%SetValueById("ValOpt",cur.ValOpt)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUNACm.opt).%New()
	else  s cur = ##class(ASUNACm.opt).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.NameOpt = Form.%page.%GetValueById("NameOpt")
		s cur.DescOpt = Form.%page.%GetValueById("DescOpt")
		s cur.ValOpt = Form.%page.%GetValueById("ValOpt")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUNACm.opt).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("NameOpt","")
	d ..%SetValueById("DescOpt","")
	d ..%SetValueById("ValOpt","")
	Quit $$$OK
}

 /// Добавить запись в базу данных. 
 ///Пример > w ##class(ASUNACm.optEdit).Add("NameOpt2","DescOpt2","ValOpt2")
ClassMethod Add(NameOpt As %String = "" ,DescOpt As %String = "" ,ValOpt As %String = "" ) As %String
{
 S o=##class(ASUNACm.opt).%New()
 s o.NameOpt=NameOpt
 s o.DescOpt=DescOpt
 s o.ValOpt=ValOpt
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
///Это конец, а где же пистолет? (c) M.Shtirlich
}

