///Эта %Zen страница предназначена для редактирования класса :ASUOMSm.Works на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
///Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
///коментарии у свойств класса ASUOMSm.Works используются следующим образом:
/// ////Текст коментария;x;f;date;w
///    1   Текст коментария - заголовок колонки и коментарий в поле редактирования
///    2   x - код сортировки поля при формирования таблицы просмотра
///    3   f - нужен ли фильтр для этого поля
///    4   date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
///    5   w - значение параметра width при формировании панели навигации
 Class ASUOMSm.WorkEdit Extends Template.TableEditor
{

///Параметр - заголовок таблицы
Parameter PAGENAME = "Новые работы-заказы";

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
tableName="ASUOMSm.Works" 
dataSource="columns" 
valueColumn="ID" 
showRowNumbers="0"
showRowSelector="0"
useSnapshot="true"
nowrap="false"
maxRows="" 
fixedHeaders="true" 
autoExecute="true" 
pageSize="5" 
onselectrow="zenPage.rowSelected();" 
>
<column colName="ID" hidden="true"/>
<column colName="NumJrnl" cellTitle="NumJrnl"  filterType="text"  filterTitle="введите контекст фильтра"  header="номер в журнале регистрации"/>
<column colName="Descr" cellTitle="Descr"  filterType="text"  filterTitle="введите контекст фильтра"  header="примечание"/>
<column colName="Order" cellTitle="Order" width="160" filterType="text"  filterTitle="введите контекст фильтра"  header="номер заказа"/>
<column colName="Works" cellTitle="Works" width="160" filterType="text"  filterTitle="введите контекст фильтра"  header="имя работы"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
/// переопределение формы редактирования полей
XData formPane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<!--<vgroup >-->
<text id="NumJrnl" label="номер в журнале регистрации" size="140" />
<text id="Descr" label="примечание" size="140" />
<text id="Order" label="номер заказа" size="140" />
<text id="Works" label="имя работы" size="140" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUOMSm.Works).%OpenId(Id)
	d Form.%page.%SetValueById("NumJrnl",cur.NumJrnl)
	d Form.%page.%SetValueById("Descr",cur.Descr)
	d Form.%page.%SetValueById("Order",cur.Order)
	d Form.%page.%SetValueById("Works",cur.Works)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUOMSm.Works).%New()
	else  s cur = ##class(ASUOMSm.Works).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.NumJrnl = Form.%page.%GetValueById("NumJrnl")
		s cur.Descr = Form.%page.%GetValueById("Descr")
		s cur.Order = Form.%page.%GetValueById("Order")
		s cur.Works = Form.%page.%GetValueById("Works")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUOMSm.Works).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("NumJrnl","")
	d ..%SetValueById("Descr","")
	d ..%SetValueById("Order","")
	d ..%SetValueById("Works","")
	Quit $$$OK
}

 /// Добавить запись в базу данных.
 ///Пример > w ##class(ASUOMSm.WorkEdit).Add("NumJrnl2","Descr2","Order2","Works2")
ClassMethod Add(NumJrnl As %String = "" ,Descr As %String = "" ,Order As %String = "" ,Works As %String = "" ) As %String
{
 S o=##class(ASUOMSm.Works).%New()
 s o.NumJrnl=NumJrnl
 s o.Descr=Descr
 s o.Order=Order
 s o.Works=Works
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
 /// Добавить запись в базу данных и возвратить новый ID в переменной newId.
 ///Пример > w ##class(ASUOMSm.WorkEdit).AddRecId(.newId,"NumJrnl2","Descr2","Order2","Works2")
ClassMethod AddRecId(newID As %String,NumJrnl As %String = "" ,Descr As %String = "" ,Order As %String = "" ,Works As %String = "" ) As %String
{
 S o=##class(ASUOMSm.Works).%New()
 s o.NumJrnl=NumJrnl
 s o.Descr=Descr
 s o.Order=Order
 s o.Works=Works
 S sc=o.%Save()
 s newID=o.%Id()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

///Эта %Zen страница предназначена для редактирования класса :ASUOOAm.CompType на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
