<text id="NPP" label="NPP" size="40" />
<text id="ENVpTYPE" label="ENV_TYPE" size="40" />
<text id="ENVSUBTYPE" label="ENVSUBTYPE" size="40" />
<text id="NUMBER" label="NUMBER" size="40" />
<text id="MADEBY" label="MADEBY" size="40" />
<text id="DETAILS" label="DETAILS" size="40" />
<text id="RELpYEAR" label="REL_YEAR" size="40" />
<text id="SALER" label="SALER" size="40" />
<text id="COMP" label="COMP" size="40" />
<!--</vgroup>-->
</pane>
}
/// заполнение полей формы редактирования записи  - должно быть переопределено в подклассах
/// Id - ид объекта
/// Form - форма редактирования полей
ClassMethod openObject(Id, Form As %ZEN.Component.form) As %Status [ ZenMethod ]
{
	s cur = ##class(ASUOOAm.InEnv).%OpenId(Id)
	d Form.%page.%SetValueById("NPP",cur.NPP)
	d Form.%page.%SetValueById("ENVpTYPE",cur.ENVpTYPE)
	d Form.%page.%SetValueById("ENVSUBTYPE",cur.ENVSUBTYPE)
	d Form.%page.%SetValueById("NUMBER",cur.NUMBER)
	d Form.%page.%SetValueById("MADEBY",cur.MADEBY)
	d Form.%page.%SetValueById("DETAILS",cur.DETAILS)
	d Form.%page.%SetValueById("RELpYEAR",cur.RELpYEAR)
	d Form.%page.%SetValueById("SALER",cur.SALER)
	d Form.%page.%SetValueById("COMP",cur.COMP)
	q $$$OK
}
/// добавление/редактирование записи(сервер) - должно быть переопределено в подклассах
ClassMethod addRowS(Id = 0, Form As %ZEN.Component.form) As %String [ ZenMethod ]
{
	i 'Id s cur = ##class(ASUOOAm.InEnv).%New()
	else  s cur = ##class(ASUOOAm.InEnv).%OpenId(Id)
	 //запись		
	//	s cur.XXX = Form.%page.CgiPar1
		s cur.NPP = Form.%page.%GetValueById("NPP")
		s cur.ENVpTYPE = Form.%page.%GetValueById("ENVpTYPE")
		s cur.ENVSUBTYPE = Form.%page.%GetValueById("ENVSUBTYPE")
		s cur.NUMBER = Form.%page.%GetValueById("NUMBER")
		s cur.MADEBY = Form.%page.%GetValueById("MADEBY")
		s cur.DETAILS = Form.%page.%GetValueById("DETAILS")
		s cur.RELpYEAR = Form.%page.%GetValueById("RELpYEAR")
		s cur.SALER = Form.%page.%GetValueById("SALER")
		s cur.COMP = Form.%page.%GetValueById("COMP")
		s sc = cur.%Save()

	i 'sc 
	{ 
		s mes = $System.Status.GetErrorText(sc)
			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
		s Id = 0
	} 
	else 
	{
		s Id = cur.%Id()
	}
	q Id
}

/// удаление записи(сервер) - должно быть переопределено в подклассах
ClassMethod delRowS(Id) As %Boolean [ ZenMethod ]
{
	s sc = ##class(ASUOOAm.InEnv).%DeleteId(Id)
	i 'sc 
	{ 
   			s mes = $tr($System.Status.GetErrorText(sc),"'","`")
 			&js<alert('#("Ошибка занесения информации! :"_$zcvt(mes,"O","JS"))#');>
 			q 0
 	}
	q 1
}

///В этом методе присваиваем значения по умолчанию
Method %OnAfterCreatePage() As %Status
{
	d ..%SetValueById("NPP","")
	d ..%SetValueById("ENVpTYPE","")
	d ..%SetValueById("ENVSUBTYPE","")
	d ..%SetValueById("NUMBER","")
	d ..%SetValueById("MADEBY","")
	d ..%SetValueById("DETAILS","")
	d ..%SetValueById("RELpYEAR","")
	d ..%SetValueById("SALER","")
	d ..%SetValueById("COMP","")
	Quit $$$OK
}

 /// Добавить запись в базу данных. 
 ///Пример > w ##class(ASUOOAm.InEnvEdit).Add("NPP2","ENVpTYPE2","ENVSUBTYPE2","NUMBER2","MADEBY2","DETAILS2","RELpYEAR2","SALER2","COMP2")
ClassMethod Add(NPP As %String = "" ,ENVpTYPE As %String = "" ,ENVSUBTYPE As %String = "" ,NUMBER As %String = "" ,MADEBY As %String = "" ,DETAILS As %String = "" ,RELpYEAR As %String = "" ,SALER As %String = "" ,COMP As %String = "" ) As %String
{
 S o=##class(ASUOOAm.InEnv).%New()
 s o.NPP=NPP
 s o.ENVpTYPE=ENVpTYPE
 s o.ENVSUBTYPE=ENVSUBTYPE
 s o.NUMBER=NUMBER
 s o.MADEBY=MADEBY
 s o.DETAILS=DETAILS
 s o.RELpYEAR=RELpYEAR
 s o.SALER=SALER
 s o.COMP=COMP
 S sc=o.%Save()
 i sc'=1 d ..ShowStatus("Ошибка :",sc)
 D o.%Close()
 q sc
}
/// показать статус или ошибку
ClassMethod ShowStatus(s As %String, sc As %String) As %String
{
 W s,+sc,! 
 I $$$ISERR(sc) D
 .D DecomposeStatus^%apiOBJ(sc,.err)
 .F i=1:1:err W err(i),!
 Q sc
}
}

///Эта %Zen страница предназначена для редактирования класса :ASUOOAm.InEnvTypes на основе
/// класса Template.TableEditor                                        (c) D.Krytenev
///Она автоматически сгенерена программой %BSCtemiate3 шаблона BsLIB   (c) S.Mikhaylenko
///коментарии у свойств класса ASUOOAm.InEnvTypes используются следующим образом:
/// ////Текст коментария;x;f;date;w
///    1   Текст коментария - заголовок колонки и коментарий в поле редактирования
///    2   x - код сортировки поля при формирования таблицы просмотра
///    3   f - нужен ли фильтр для этого поля
///    4   date - свойство имеет тип %String, но там лежит дата в формате ГГГГММДД
///    5   w - значение параметра width при формировании панели навигации
 Class ASUOOAm.InEnvTypesEdit Extends Template.TableEditor
{

///Параметр - заголовок таблицы
Parameter PAGENAME = " Типы комплектующих";

/// Свойство - заоловок таблицы
Property PageTitle As %ZEN.Datatype.string [ InitialExpression = {..#PAGENAME} ];

/// Свойство - панель таблицы
Property TablePaneId As %ZEN.Datatype.string [ InitialExpression = "table", ReadOnly ];

 /// переопределение таблицы
XData tablePane [ XMLNamespace = "http://www.intersystems.com/zen" ]
{
<pane xmlns="http://www.intersystems.com/zen">
<tablePane id="table"
