<column colName="NPP"  filterType="text"  filterTitle="введите контекст фильтра"  header="NPP"/>
<column colName="NAME"  filterType="text"  filterTitle="введите контекст фильтра"  header="NAME"/>
</tablePane>
<tableNavigator tablePaneId="table"/>
</pane>
}
