/// Добавить запись в базу данных. /// Пример > w ##class(ASUPFOm.WorkProductEdit).Add("WorkName2","WorkType2","WorkOrder2","CreateDate2","CreateGod2","Graf2","ProdDec2","ProdName2","ProdCount2","ProdNumTeh2","ProdNumKon2","ProdNumNor2","ProdMat2","ProdPar2","ProdSN2","ProdInd2","ProdTema2","RecUser2","RecDat2","RecStatus2","RecSours2") ClassMethod Add(WorkName As %String = "", WorkType As %String = "", WorkOrder As %String = "", CreateDate As %String = "", CreateGod As %String = "", Graf As %String = "", ProdDec As %String = "", ProdName As %String = "", ProdCount As %String = "", ProdNumTeh As %String = "", ProdNumKon As %String = "", ProdNumNor As %String = "", ProdMat As %String = "", ProdPar As %String = "", ProdSN As %String = "", ProdInd As %String = "", ProdTema As %String = "", RecUser As %String = "", RecDat As %String = "", RecStatus As %String = "", RecSours As %String = "") As %String { S o=##class(ASUPFOm.WorkProduct).%New() s o.WorkName=WorkName s o.WorkType=WorkType s o.WorkOrder=WorkOrder s o.CreateDate=CreateDate s o.CreateGod=CreateGod s o.Graf=Graf s o.ProdDec=ProdDec s o.ProdName=ProdName s o.ProdCount=ProdCount s o.ProdNumTeh=ProdNumTeh s o.ProdNumKon=ProdNumKon s o.ProdNumNor=ProdNumNor s o.ProdMat=ProdMat s o.ProdPar=ProdPar s o.ProdSN=ProdSN s o.ProdInd=ProdInd s o.ProdTema=ProdTema s o.RecUser=RecUser s o.RecDat=RecDat s o.RecStatus=RecStatus s o.RecSours=RecSours S sc=o.%Save() i sc'=1 d ..ShowStatus("Ошибка :",sc) D o.%Close() q sc }
/// Добавить запись в базу данных и возвратить новый ID в переменной newId. /// Пример > w ##class(ASUPFOm.WorkProductEdit).AddRecId(.newId,"WorkName2","WorkType2","WorkOrder2","CreateDate2","CreateGod2","Graf2","ProdDec2","ProdName2","ProdCount2","ProdNumTeh2","ProdNumKon2","ProdNumNor2","ProdMat2","ProdPar2","ProdSN2","ProdInd2","ProdTema2","RecUser2","RecDat2","RecStatus2","RecSours2") ClassMethod AddRecId(newID As %String, WorkName As %String = "", WorkType As %String = "", WorkOrder As %String = "", CreateDate As %String = "", CreateGod As %String = "", Graf As %String = "", ProdDec As %String = "", ProdName As %String = "", ProdCount As %String = "", ProdNumTeh As %String = "", ProdNumKon As %String = "", ProdNumNor As %String = "", ProdMat As %String = "", ProdPar As %String = "", ProdSN As %String = "", ProdInd As %String = "", ProdTema As %String = "", RecUser As %String = "", RecDat As %String = "", RecStatus As %String = "", RecSours As %String = "") As %String { S o=##class(ASUPFOm.WorkProduct).%New() s o.WorkName=WorkName s o.WorkType=WorkType s o.WorkOrder=WorkOrder s o.CreateDate=CreateDate s o.CreateGod=CreateGod s o.Graf=Graf s o.ProdDec=ProdDec s o.ProdName=ProdName s o.ProdCount=ProdCount s o.ProdNumTeh=ProdNumTeh s o.ProdNumKon=ProdNumKon s o.ProdNumNor=ProdNumNor s o.ProdMat=ProdMat s o.ProdPar=ProdPar s o.ProdSN=ProdSN s o.ProdInd=ProdInd s o.ProdTema=ProdTema s o.RecUser=RecUser s o.RecDat=RecDat s o.RecStatus=RecStatus s o.RecSours=RecSours S sc=o.%Save() s newID=o.%Id() i sc'=1 d ..ShowStatus("Ошибка :",sc) D o.%Close() q sc }
